/**
 * Player class representing a game character
 * 
 * @class
 * @property {string} name - Player's name
 * @property {number} health - Current health points
 * @property {number} level - Player's level
 */
class Player {
  constructor(name, health, level) {
    this.name = name;
    this.health = health;
    this.level = level;
  }

  /**
   * Makes the player take damage
   * 
   * @param {number} amount - Damage amount
   * @returns {boolean} Whether player survived
   */
  takeDamage(amount) {
    this.health -= amount;
    return this.health > 0;
  }
}

