solutionCode:
# Define base Character class
class Character:
    def __init__(self, name, health):
        self.name = name
        self.health = health

    def attack(self):
        print(f"{self.name} attacks!")

# Define Mage class that inherits from Character
class Mage(Character):
    def __init__(self, name, health, mana):
        super().__init__(name, health)
        self.mana = mana

    def cast_spell(self):
        print(f"{self.name} casts a spell!")

# Create a mage and test both methods
mage = Mage("Gandalf", 80, 100)
mage.attack()  # Inherited method
mage.cast_spell()  # New method
