solutionCode:
// Method 1: HealPlayer
static int HealPlayer(int amount)
{
    Console.WriteLine("Healed for " + amount + " health!");
    return amount;
}

// Method 2: CalculateTotalGold
static int CalculateTotalGold(int coins, int treasure)
{
    int total = coins + treasure;
    return total;
}

// Method 3: DisplayStats
static void DisplayStats(string name, int health, int gold)
{
    Console.WriteLine("=== Character Stats ===");
    Console.WriteLine("Name: " + name);
    Console.WriteLine("Health: " + health);
    Console.WriteLine("Gold: " + gold);
}

// Test your methods
int healing = HealPlayer(25);
int totalGold = CalculateTotalGold(50, 100);
DisplayStats("Knight", 100, totalGold);
