// Define the CalculatePower method
int CalculatePower(int level, int strength)
{
    return level * strength + 20;
}

// Call the method and store the result
int totalPower = CalculatePower(7, 12);

// Print the battle power
Console.WriteLine($"Battle Power: {totalPower}");
