solutionCode:
using System;

class Program
{
    static void Main()
    {
        // Create your inventory array here
        string[] inventory = { "sword", "shield", "potion" };

        // Print the first item (sword)
        Console.WriteLine(inventory[0]);

        // Print the third item (potion)
        Console.WriteLine(inventory[2]);

        // Print the entire array
        Console.WriteLine(string.Join(", ", inventory));
    }
}
