solutionCode:
using System;
using System.Collections.Generic;

class Program
{
    static void Main()
    {
        // Create your backpack list
        List<string> backpack = new List<string> { "map", "compass" };

        // Add rope to the end
        backpack.Add("rope");

        // Insert lantern at the beginning (index 0)
        backpack.Insert(0, "lantern");

        // Print the number of items
        Console.WriteLine(backpack.Count);

        // Remove compass
        backpack.Remove("compass");

        // Print the final backpack
        Console.WriteLine(string.Join(", ", backpack));
    }
}
