using System;
using System.Collections.Generic;

class Program
{
    static void Main()
    {
        // Create your character dictionary
        Dictionary<string, object> character = new Dictionary<string, object>
        {
            { "name", "Valor" },
            { "health", 100 },
            { "weapon", "Excalibur" }
        };

        // Print the character's name
        Console.WriteLine(character["name"]);

        // Print the character's weapon
        Console.WriteLine(character["weapon"]);

        // Add armor
        character["armor"] = "Plate Mail";

        // Update health to 150
        character["health"] = 150;

        // Print the number of properties
        Console.WriteLine(character.Count);
    }
}
