solutionCode:
using System;

class Program
{
    public class Weapon
    {
        public string Name { get; set; }
        public int Damage { get; set; }
        public string Type { get; set; }
    }

    static void Main()
    {
        // Create a new Weapon object called excalibur
        Weapon excalibur = new Weapon
        {
            Name = "Excalibur",
            Damage = 50,
            Type = "Sword"
        };

        // Print the weapon's name
        Console.WriteLine(excalibur.Name);

        // Print the weapon's damage
        Console.WriteLine(excalibur.Damage);

        // Print the weapon's type
        Console.WriteLine(excalibur.Type);

        // Update damage to 75
        excalibur.Damage = 75;

        // Print the updated damage
        Console.WriteLine(excalibur.Damage);
    }
}
