solutionCode:
using System;

class Program
{
    // Create your Item class here
    public class Item
    {
        public string Name { get; set; }
        public string Type { get; set; }
        public int Value { get; set; }
        public int Weight { get; set; }
    }

    static void Main()
    {
        // Create sword item
        Item sword = new Item
        {
            Name = "Iron Sword",
            Type = "Weapon",
            Value = 50,
            Weight = 10
        };

        // Create potion item
        Item potion = new Item
        {
            Name = "Health Potion",
            Type = "Consumable",
            Value = 25,
            Weight = 2
        };

        // Print sword information
        Console.WriteLine($"{sword.Name} ({sword.Type}) - Value: {sword.Value}g, Weight: {sword.Weight}kg");

        // Print potion information
        Console.WriteLine($"{potion.Name} ({potion.Type}) - Value: {potion.Value}g, Weight: {potion.Weight}kg");

        // Calculate and print totals
        int totalValue = sword.Value + potion.Value;
        int totalWeight = sword.Weight + potion.Weight;
        Console.WriteLine($"\nTotal Value: {totalValue}g");
        Console.WriteLine($"Total Weight: {totalWeight}kg");
    }
}
