solutionCode:
using System;

class Program
{
    static void Main()
    {
        // Ask for character name
        Console.Write("Enter your character's name: ");
        string name = Console.ReadLine();

        // Ask for character level
        Console.Write("Enter your level: ");
        int level = int.Parse(Console.ReadLine());

        // Ask for starting gold
        Console.Write("Enter your starting gold: ");
        int gold = int.Parse(Console.ReadLine());

        // Display welcome message
        Console.WriteLine($"\nWelcome, {name}!");
        Console.WriteLine($"Level: {level} | Gold: {gold}");

        // Show name in uppercase
        Console.WriteLine($"Hero name in caps: {name.ToUpper()}");

        // Show name length
        Console.WriteLine($"Name length: {name.Length} characters");
    }
}
