solutionCode:
using System;

class Program
{
    static void Main()
    {
        Console.WriteLine("=== DAMAGE CALCULATOR ===");

        // Set base damage and multiplier
        int baseDamage = 25;
        double strengthMultiplier = 1.5;

        // Calculate total damage (round the result)
        int totalDamage = (int)Math.Round(baseDamage * strengthMultiplier);

        // Set player health and enemy damage
        int playerHealth = 200;
        int enemyDamage = 45;

        // Calculate health after one hit
        int healthAfterHit = playerHealth - enemyDamage;

        // Calculate hits until defeat (use ceiling)
        int hitsUntilDefeat = (int)Math.Ceiling((double)playerHealth / enemyDamage);

        // Demonstrate Math.Max with minimum damage
        int weakDamage = 5;
        int guaranteedDamage = Math.Max(weakDamage, 10);

        Console.WriteLine($"Base Damage: {baseDamage}");
        Console.WriteLine($"Total Damage: {totalDamage}");
        Console.WriteLine($"Health After Hit: {healthAfterHit}");
        Console.WriteLine($"Hits Until Defeat: {hitsUntilDefeat}");
        Console.WriteLine($"Minimum Damage Applied: {guaranteedDamage}");
    }
}
