solutionCode:
using System;

class Program
{
    static void Main()
    {
        // CHALLENGE: Character Profile Creator

        // Get user input
        Console.Write("Enter your character's name: ");
        string characterName = Console.ReadLine();

        Console.Write("Enter your character's age: ");
        int age = int.Parse(Console.ReadLine());

        Console.Write("Enter your starting gold: ");
        int startingGold = int.Parse(Console.ReadLine());

        // Calculate gold after purchase
        int swordCost = 50;
        int goldAfterPurchase = startingGold - swordCost;

        // Print character profile
        Console.WriteLine();
        Console.WriteLine("=== CHARACTER PROFILE ===");
        Console.WriteLine($"Name: {characterName}");
        Console.WriteLine($"Age: {age} years old");
        Console.WriteLine($"Starting Gold: {startingGold}");
        Console.WriteLine($"Gold after buying sword: {goldAfterPurchase}");
        Console.WriteLine($"Welcome to the adventure, {characterName}!");
    }
}

