solutionCode:
using System;

class Program
{
    static void Main()
    {
        // Merchant Purchase System

        // Set up variables
        int playerGold = 75;
        int itemCost = 50;
        string itemName = "Steel Sword";

        // Check if player can afford the item
        if (playerGold >= itemCost)
        {
            // Player has enough gold
            int remainingGold = playerGold - itemCost;
            Console.WriteLine($"Purchase successful! You bought {itemName}.");
            Console.WriteLine($"Gold remaining: {remainingGold}");
        }
        else
        {
            // Player doesn't have enough gold
            int goldNeeded = itemCost - playerGold;
            Console.WriteLine($"Not enough gold for {itemName}!");
            Console.WriteLine($"You need {goldNeeded} more gold.");
        }
    }
}
