solutionCode:
using System;

class Program
{
    static void Main()
    {
        Console.WriteLine("=== QUEST ELIGIBILITY CHECKER ===");

        // Get player info
        Console.Write("Enter your level: ");
        int level = int.Parse(Console.ReadLine());

        Console.Write("Enter your gold: ");
        int gold = int.Parse(Console.ReadLine());

        Console.Write("Are you in a guild? (yes/no): ");
        string guildInput = Console.ReadLine().ToLower();
        bool hasGuild = (guildInput == "yes");

        bool inCombat = false;  // Assume not in combat

        Console.WriteLine("\n=== ELIGIBLE QUESTS ===");

        // Check Epic Quest (Level >= 15 AND Gold >= 1000)
        if (level >= 15 && gold >= 1000)
        {
            Console.WriteLine("✓ Epic Quest: The Dragon's Lair");
        }

        // Check Guild Quest (Has guild OR Level >= 20)
        if (hasGuild || level >= 20)
        {
            Console.WriteLine("✓ Guild Quest: Brotherhood Trials");
        }

        // Check Starter Quest (Level < 5 AND Gold < 100)
        if (level < 5 && gold < 100)
        {
            Console.WriteLine("✓ Starter Quest: First Steps");
        }

        // Check Any Quest (NOT in combat)
        if (!inCombat)
        {
            Console.WriteLine("✓ General Quest: Available when not in combat");
        }
    }
}
