solutionCode:
using System;

class Program
{
    static void Main()
    {
        // CHALLENGE: Quest Recommendation System

        Console.WriteLine("=== QUEST RECOMMENDATION SYSTEM ===");

        // Get player stats
        Console.Write("Enter your name: ");
        string playerName = Console.ReadLine();

        Console.Write("Enter your level: ");
        int level = int.Parse(Console.ReadLine());

        Console.Write("Enter your gold: ");
        int gold = int.Parse(Console.ReadLine());

        Console.Write("Enter your class (Warrior/Mage/Rogue): ");
        string playerClass = Console.ReadLine();

        Console.Write("Have you completed the tutorial? (yes/no): ");
        bool tutorialComplete = Console.ReadLine().ToLower() == "yes";

        // Display player stats
        Console.WriteLine("\n=== PLAYER STATS ===");
        Console.WriteLine($"Name: {playerName}");
        Console.WriteLine($"Level: {level}");
        Console.WriteLine($"Gold: {gold}");
        Console.WriteLine($"Class: {playerClass}");
        Console.WriteLine($"Tutorial Complete: {(tutorialComplete ? "Yes" : "No")}");

        // Check quest eligibility
        Console.WriteLine("\n=== ELIGIBLE QUESTS ===");
        int questCount = 0;
        string recommendedQuest = "None";

        // Tutorial Quest
        if (level < 5 && !tutorialComplete)
        {
            Console.WriteLine("✓ Tutorial Quest: First Steps");
            questCount++;
            recommendedQuest = "Tutorial Quest (complete this first!)";
        }

        // Warrior Quest
        if (playerClass == "Warrior" && level >= 10)
        {
            Console.WriteLine("✓ Warrior Quest: Blade of Honor");
            questCount++;
            recommendedQuest = "Warrior Quest (matches your class!)";
        }

        // Mage Quest
        if (playerClass == "Mage" && level >= 10)
        {
            Console.WriteLine("✓ Mage Quest: The Arcane Tower");
            questCount++;
            recommendedQuest = "Mage Quest (matches your class!)";
        }

        // Rogue Quest
        if (playerClass == "Rogue" && level >= 10)
        {
            Console.WriteLine("✓ Rogue Quest: Shadows and Secrets");
            questCount++;
            recommendedQuest = "Rogue Quest (matches your class!)";
        }

        // Rich Merchant Quest
        if (gold >= 500)
        {
            Console.WriteLine("✓ Rich Merchant Quest: Golden Opportunity");
            questCount++;
        }

        // Epic Raid
        if (level >= 20 && gold >= 1000 && tutorialComplete)
        {
            Console.WriteLine("✓ Epic Raid: Dragon's Lair");
            questCount++;
            recommendedQuest = "Epic Raid (ultimate challenge!)";
        }

        // Beginner Friendly
        if (level < 10 && tutorialComplete)
        {
            Console.WriteLine("✓ Beginner Friendly: Village Defense");
            questCount++;
            if (recommendedQuest == "None")
            {
                recommendedQuest = "Beginner Friendly (great for your level!)";
            }
        }

        // Summary
        Console.WriteLine($"\nTotal Eligible: {questCount} quests");
        Console.WriteLine($"Recommended: {recommendedQuest}");
    }
}
