solutionCode:
using System;

class Program
{
    static void Main()
    {
        // Power-Up Leveling System

        Console.WriteLine("=== LEVEL UP SEQUENCE ===");
        // Loop through levels 1 to 10
        for (int level = 1; level <= 10; level++)
        {
            int damage = level * 5;
            Console.WriteLine($"Level {level}: {damage} damage");
        }

        Console.WriteLine("\n=== COUNTDOWN TIMER ===");
        // Create countdown from 5 to 1
        for (int count = 5; count >= 1; count--)
        {
            Console.WriteLine($"{count}...");
        }
        Console.WriteLine("Blast off!");

        Console.WriteLine("\n=== TOTAL XP CALCULATION ===");
        int totalXp = 0;
        // Calculate total XP from levels 1-10
        for (int level = 1; level <= 10; level++)
        {
            int xp = level * 100;
            totalXp += xp;
        }

        Console.WriteLine($"Total XP earned from levels 1-10: {totalXp}");
    }
}
