solutionCode:
using System;

class Program
{
    static void Main()
    {
        Console.WriteLine("=== HEALING SIMULATION ===");
        int health = 50;
        int healAmount = 10;
        int turn = 1;

        while (health < 100)
        {
            Console.WriteLine($"Turn {turn}: Health = {health}");
            health += healAmount;
            turn++;
        }
        Console.WriteLine($"Fully healed! Final health: {health}\n");

        Console.WriteLine("=== RESOURCE GATHERING ===");
        int gold = 0;
        int round = 1;

        while (gold < 200)
        {
            gold += 45;
            Console.WriteLine($"Round {round}: Gathered gold. Total: {gold}");
            round++;
        }
        Console.WriteLine($"Goal reached! Total gold: {gold}\n");

        Console.WriteLine("=== ENEMY WAVES ===");
        int wave = 1;

        while (wave <= 5)
        {
            Console.WriteLine($"Fighting wave {wave}...");
            wave++;
        }
        Console.WriteLine("All waves defeated!\n");

        Console.WriteLine("=== LEVEL GRIND ===");
        int level = 1;

        while (level < 15)
        {
            Console.WriteLine($"Current level: {level}");
            level++;
        }
        Console.WriteLine($"Max level reached: {level}");
    }
}
