using System;
using System.Collections.Generic;
using System.Linq;

class Program
{
    public class Item
    {
        public string Name { get; set; }
        public string Type { get; set; }
        public int Value { get; set; }
    }

    static void AddItem(List<Item> inventory, string name, string type, int value)
    {
        inventory.Add(new Item { Name = name, Type = type, Value = value });
    }

    static bool RemoveItem(List<Item> inventory, string name)
    {
        Item item = inventory.FirstOrDefault(i => i.Name == name);
        if (item != null)
        {
            inventory.Remove(item);
            return true;
        }
        return false;
    }

    static Item FindItem(List<Item> inventory, string name)
    {
        return inventory.FirstOrDefault(i => i.Name == name);
    }

    static int GetTotalValue(List<Item> inventory)
    {
        return inventory.Sum(i => i.Value);
    }

    static List<Item> GetItemsByType(List<Item> inventory, string type)
    {
        return inventory.Where(i => i.Type == type).ToList();
    }

    static List<Item> SortByValue(List<Item> inventory)
    {
        return inventory.OrderByDescending(i => i.Value).ToList();
    }

    static void Main()
    {
        List<Item> inventory = new List<Item>();

        Console.WriteLine("=== STARTING INVENTORY ===");
        AddItem(inventory, "Iron Sword", "weapon", 100);
        AddItem(inventory, "Leather Armor", "armor", 75);
        AddItem(inventory, "Health Potion", "potion", 25);

        for (int i = 0; i < inventory.Count; i++)
        {
            Item item = inventory[i];
            Console.WriteLine($"{i + 1}. {item.Name} ({item.Type}) - {item.Value}g");
        }

        Console.WriteLine("\n=== ADDING ITEMS ===");
        AddItem(inventory, "Steel Sword", "weapon", 150);
        AddItem(inventory, "Magic Ring", "misc", 200);
        Console.WriteLine("Added Steel Sword");
        Console.WriteLine("Added Magic Ring");

        Console.WriteLine("\n=== REMOVING ITEM ===");
        if (RemoveItem(inventory, "Health Potion"))
        {
            Console.WriteLine("Removed Health Potion");
        }

        Console.WriteLine("\n=== FINDING ITEM ===");
        Item found = FindItem(inventory, "Iron Sword");
        if (found != null)
        {
            Console.WriteLine($"Found: {found.Name} ({found.Type}) - {found.Value}g");
        }

        Console.WriteLine("\n=== TOTAL VALUE ===");
        int total = GetTotalValue(inventory);
        Console.WriteLine($"Total inventory value: {total}g");

        Console.WriteLine("\n=== WEAPONS ===");
        List<Item> weapons = GetItemsByType(inventory, "weapon");
        foreach (var weapon in weapons)
        {
            Console.WriteLine($"- {weapon.Name} ({weapon.Value}g)");
        }

        Console.WriteLine("\n=== SORTED BY VALUE ===");
        List<Item> sorted = SortByValue(inventory);
        for (int i = 0; i < sorted.Count; i++)
        {
            Console.WriteLine($"{i + 1}. {sorted[i].Name} - {sorted[i].Value}g");
        }
    }
}
