using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.Json;

class Program
{
    public class Character
    {
        public string Name { get; set; }
        public string Class { get; set; }
        public int Level { get; set; }
        public int Xp { get; set; }
        public int Health { get; set; }
        public int MaxHealth { get; set; }
        public int Mana { get; set; }
        public int Strength { get; set; }
        public int Intelligence { get; set; }
        public int Gold { get; set; }
        public List<Item> Inventory { get; set; } = new List<Item>();
    }

    public class Item
    {
        public string Name { get; set; }
        public string Type { get; set; }
        public int Value { get; set; }
    }

    public class Quest
    {
        public string Name { get; set; }
        public string Status { get; set; }
        public int Reward { get; set; }
        public int Xp { get; set; }
    }

    public class Enemy
    {
        public string Name { get; set; }
        public int Health { get; set; }
        public int Damage { get; set; }
        public int Reward { get; set; }
        public int Xp { get; set; }
    }

    static Character CreateCharacter(string name, string charClass)
    {
        var character = new Character
        {
            Name = name,
            Class = charClass,
            Level = 1,
            Xp = 0,
            MaxHealth = 100,
            Health = 100,
            Mana = 50,
            Strength = charClass == "Warrior" ? 15 : 10,
            Intelligence = charClass == "Mage" ? 15 : 10,
            Gold = 100
        };
        character.Inventory.Add(new Item { Name = "Starter Sword", Type = "weapon", Value = 50 });
        return character;
    }

    static void LevelUpCharacter(Character character)
    {
        character.Level++;
        character.MaxHealth += 20;
        character.Health = character.MaxHealth;
        character.Mana += 10;
        character.Strength += 2;
        character.Intelligence += 2;
        Console.WriteLine($"\nüéâ LEVEL UP! Now level {character.Level}!");
        Console.WriteLine($"  +20 Max HP | +10 Mana | +2 Strength | +2 Intelligence");
    }

    static void DisplayCharacter(Character character)
    {
        Console.WriteLine("\n" + new string('=', 40));
        Console.WriteLine($"  {character.Name} the {character.Class}");
        Console.WriteLine(new string('=', 40));
        Console.WriteLine($"Level: {character.Level} | XP: {character.Xp}/1000");
        Console.WriteLine($"Health: {character.Health}/{character.MaxHealth} | Mana: {character.Mana}");
        Console.WriteLine($"Strength: {character.Strength} | Intelligence: {character.Intelligence}");
        Console.WriteLine($"Gold: {character.Gold}g");
        Console.WriteLine($"Inventory Items: {character.Inventory.Count}");
    }

    static void AddItem(Character character, string name, string type, int value)
    {
        character.Inventory.Add(new Item { Name = name, Type = type, Value = value });
    }

    static void DisplayInventory(Character character)
    {
        Console.WriteLine("\n=== INVENTORY ===");
        if (character.Inventory.Count == 0)
        {
            Console.WriteLine("Empty");
            return;
        }

        var sorted = character.Inventory.OrderByDescending(i => i.Value);
        int i = 1;
        foreach (var item in sorted)
        {
            Console.WriteLine($"{i}. {item.Name} ({item.Type}) - {item.Value}g");
            i++;
        }

        int totalValue = character.Inventory.Sum(item => item.Value);
        Console.WriteLine($"\nTotal Value: {totalValue}g");
    }

    static void DisplayQuests(List<Quest> quests)
    {
        Console.WriteLine("\n=== QUEST LOG ===");
        for (int i = 0; i < quests.Count; i++)
        {
            string icon = quests[i].Status == "Completed" ? "‚úì" : "‚óã";
            Console.WriteLine($"{i + 1}. {icon} {quests[i].Name} - {quests[i].Reward}g, {quests[i].Xp} XP [{quests[i].Status}]");
        }
    }

    static void CompleteQuest(List<Quest> quests, int questIndex, Character character)
    {
        if (questIndex < 0 || questIndex >= quests.Count) return;

        Quest quest = quests[questIndex];
        if (quest.Status == "Completed")
        {
            Console.WriteLine("Quest already completed!");
            return;
        }

        quest.Status = "Completed";
        character.Gold += quest.Reward;
        character.Xp += quest.Xp;

        Console.WriteLine($"\n‚úì Quest Completed: {quest.Name}");
        Console.WriteLine($"  Rewards: +{quest.Reward}g, +{quest.Xp} XP");

        if (character.Xp >= 1000)
        {
            character.Xp -= 1000;
            LevelUpCharacter(character);
        }
    }

    static bool Battle(Character character, Enemy enemy)
    {
        Console.WriteLine($"\n‚öîÔ∏è BATTLE: {character.Name} vs {enemy.Name}");
        Console.WriteLine(new string('=', 40));

        int enemyHp = enemy.Health;
        int turn = 1;

        while (enemyHp > 0 && character.Health > 0)
        {
            Console.WriteLine($"\nTurn {turn}");
            Console.WriteLine($"  Your HP: {character.Health} | {enemy.Name} HP: {enemyHp}");

            // Player attacks
            int damage = character.Strength + new Random().Next(5, 16);
            enemyHp -= damage;
            Console.WriteLine($"  You deal {damage} damage!");

            if (enemyHp <= 0)
            {
                Console.WriteLine($"\nüéâ Victory! {enemy.Name} defeated!");
                character.Gold += enemy.Reward;
                character.Xp += enemy.Xp;
                Console.WriteLine($"  Rewards: +{enemy.Reward}g, +{enemy.Xp} XP");

                if (character.Xp >= 1000)
                {
                    character.Xp -= 1000;
                    LevelUpCharacter(character);
                }
                return true;
            }

            // Enemy attacks
            int enemyDamage = enemy.Damage + new Random().Next(0, 11);
            character.Health -= enemyDamage;
            Console.WriteLine($"  {enemy.Name} deals {enemyDamage} damage!");

            if (character.Health <= 0)
            {
                Console.WriteLine("\nüíÄ Defeated! Game Over.");
                return false;
            }

            turn++;
        }

        return true;
    }

    static void Main()
    {
        Console.WriteLine("\n" + new string('=', 50));
        Console.WriteLine("RPG MANAGEMENT SYSTEM".PadLeft(35));
        Console.WriteLine(new string('=', 50) + "\n");

        // Character creation
        Console.Write("Enter your name: ");
        string name = Console.ReadLine();
        Console.WriteLine("\nChoose class: 1. Warrior  2. Mage");
        Console.Write("Enter choice (1-2): ");
        string choice = Console.ReadLine();
        string charClass = choice == "1" ? "Warrior" : "Mage";

        Character character = CreateCharacter(name, charClass);

        // Initialize quests
        List<Quest> quests = new List<Quest>
        {
            new Quest { Name = "Defeat Goblin King", Status = "Available", Reward = 500, Xp = 300 },
            new Quest { Name = "Find Lost Artifact", Status = "Available", Reward = 1000, Xp = 500 }
        };

        // Enemies
        List<Enemy> enemies = new List<Enemy>
        {
            new Enemy { Name = "Goblin", Health = 50, Damage = 8, Reward = 50, Xp = 100 },
            new Enemy { Name = "Orc", Health = 80, Damage = 12, Reward = 100, Xp = 200 }
        };

        // Main game loop
        bool playing = true;
        while (playing)
        {
            Console.WriteLine("\n" + new string('=', 40));
            Console.WriteLine("  MAIN MENU");
            Console.WriteLine(new string('=', 40));
            Console.WriteLine("1. View Character");
            Console.WriteLine("2. View Inventory");
            Console.WriteLine("3. View Quests");
            Console.WriteLine("4. Complete Quest");
            Console.WriteLine("5. Battle");
            Console.WriteLine("6. Rest (restore health)");
            Console.WriteLine("7. Exit");

            Console.Write("\nEnter choice: ");
            choice = Console.ReadLine();

            switch (choice)
            {
                case "1":
                    DisplayCharacter(character);
                    break;
                case "2":
                    DisplayInventory(character);
                    break;
                case "3":
                    DisplayQuests(quests);
                    break;
                case "4":
                    DisplayQuests(quests);
                    Console.Write("\nWhich quest to complete (enter number): ");
                    if (int.TryParse(Console.ReadLine(), out int questNum))
                    {
                        CompleteQuest(quests, questNum - 1, character);
                    }
                    break;
                case "5":
                    Console.WriteLine("\nChoose enemy:");
                    for (int i = 0; i < enemies.Count; i++)
                    {
                        Console.WriteLine($"{i + 1}. {enemies[i].Name} (HP: {enemies[i].Health}, Damage: {enemies[i].Damage})");
                    }
                    Console.Write("Enter choice: ");
                    if (int.TryParse(Console.ReadLine(), out int enemyChoice) && enemyChoice > 0 && enemyChoice <= enemies.Count)
                    {
                        if (!Battle(character, enemies[enemyChoice - 1]))
                        {
                            playing = false;
                        }
                    }
                    break;
                case "6":
                    character.Health = character.MaxHealth;
                    Console.WriteLine("\nüí§ Rested! Health fully restored.");
                    break;
                case "7":
                    Console.WriteLine("\nThanks for playing! Your adventure continues...");
                    playing = false;
                    break;
            }
        }

        Console.WriteLine("\n" + new string('=', 40));
        Console.WriteLine("  FINAL STATS");
        Console.WriteLine(new string('=', 40));
        DisplayCharacter(character);
    }
}
