# Combat Decision System

var player_health = 30
var has_healing_potion = true
var enemy_nearby = true

# Check health level and decide what to do
if player_health < 20:
    print("CRITICAL HEALTH!")
    if has_healing_potion:
        print("Using healing potion!")
        player_health += 50
        print("Health restored to: ", player_health)
    else:
        print("No potion! Retreat!")
elif player_health < 50:
    print("Health is low")
    if enemy_nearby:
        print("Be careful in combat!")
    else:
        print("Safe to explore")
else:
    print("Health is good!")
    if enemy_nearby:
        print("Ready for battle!")
    else:
        print("Continue exploring")
