# Quest Eligibility Challenge

# Player stats
var level = 12
var has_tutorial = true
var gold = 75
var has_rare_item = false
var health = 90
var has_magic_sword = true
var has_shield = false
var bronze_done = true
var silver_done = false

print("=== QUEST BOARD ===")

# Bronze Quest
if level >= 5 and has_tutorial:
    if bronze_done:
        print("[COMPLETED] Bronze Quest")
    else:
        print("[AVAILABLE] Bronze Quest")
else:
    print("[LOCKED] Bronze Quest")

# Silver Quest
if level >= 10 and (gold >= 50 or has_rare_item) and bronze_done:
    if silver_done:
        print("[COMPLETED] Silver Quest")
    else:
        print("[AVAILABLE] Silver Quest")
else:
    print("[LOCKED] Silver Quest")

# Gold Quest
if level >= 20 and health > 80 and has_magic_sword and has_shield and bronze_done and silver_done:
    print("[AVAILABLE] Gold Quest")
else:
    print("[LOCKED] Gold Quest - Requirements:")
    if level < 20:
        print("  - Need level 20")
    if not (has_magic_sword and has_shield):
        print("  - Need magic sword AND shield")
    if not (bronze_done and silver_done):
        print("  - Complete previous quests")
