# Return Value Challenges

# 1. Calculate total power
func calculate_power(strength: int, intelligence: int) -> int:
    var power = strength * 2 + intelligence
    return power

# 2. Get status based on health %
func get_health_status(current: int, maximum: int) -> String:
    var percent = float(current) / float(maximum) * 100
    if percent > 75:
        return "Healthy"
    elif percent > 40:
        return "Injured"
    elif percent > 10:
        return "Critical"
    else:
        return "Near Death"

# 3. Create item dictionary
func create_item(name: String, value: int) -> Dictionary:
    return {
        "name": name,
        "value": value,
        "stackable": value < 100
    }

# Test functions
var power = calculate_power(20, 15)
print("Total power: ", power)

var status = get_health_status(60, 100)
print("Health status: ", status)

var potion = create_item("Health Potion", 50)
print("Item: ", potion["name"], " - Value: ", potion["value"])
