# CHALLENGE: Quest Eligibility Checker

# Get player information
player_level = int(input("Enter your level: "))
sword_input = input("Do you have a sword? (yes/no): ").lower()
has_sword = (sword_input == "yes")
current_health = int(input("Enter your current health: "))

print("\n=== AVAILABLE QUESTS ===")

# Easy Quest - anyone can do it
if player_level >= 1:
    print("✓ Easy Quest: Gather herbs")
else:
    print("✗ Easy Quest: Gather herbs")
    print("  Reason: You need to be at least level 1")

# Medium Quest - needs level 5 and sword
if player_level >= 5 and has_sword:
    print("✓ Medium Quest: Defeat the goblin chief")
else:
    print("✗ Medium Quest: Defeat the goblin chief")
    if player_level < 5:
        print(f"  Reason: Level too low (need 5, you are {player_level})")
    if not has_sword:
        print("  Reason: You need a sword")

# Hard Quest - needs level 10, sword, and health > 50
if player_level >= 10 and has_sword and current_health > 50:
    print("✓ Hard Quest: Slay the dragon")
else:
    print("✗ Hard Quest: Slay the dragon")
    if player_level < 10:
        print(f"  Reason: Level too low (need 10, you are {player_level})")
    if not has_sword:
        print("  Reason: You need a sword")
    if current_health <= 50:
        print(f"  Reason: Health too low (need 50+, you have {current_health})")
