# Method Overriding System - Solution

class Character:
    def __init__(self, name, health):
        self.name = name
        self.health = health

    def attack(self):
        return 10

    def defend(self):
        return 5

    def special_ability(self):
        return "No special ability"

    def __str__(self):
        return f"{self.name} ({self.health} HP)"

class Warrior(Character):
    def __init__(self, name, health, strength, armor):
        super().__init__(name, health)
        self.strength = strength
        self.armor = armor

    def attack(self):
        return self.strength * 3

    def defend(self):
        return super().defend() + self.armor

    def special_ability(self):
        return "Shield Bash"

    def __str__(self):
        return super().__str__() + " (Warrior)"

class Mage(Character):
    def __init__(self, name, health, intelligence, mana):
        super().__init__(name, health)
        self.intelligence = intelligence
        self.mana = mana

    def attack(self):
        return self.intelligence * 2

    def defend(self):
        return super().defend() + (self.mana / 10)

    def special_ability(self):
        return "Arcane Blast"

    def __str__(self):
        return super().__str__() + " (Mage)"

class Rogue(Character):
    def __init__(self, name, health, agility):
        super().__init__(name, health)
        self.agility = agility

    def attack(self):
        return self.agility * 4

    def defend(self):
        return self.agility * 2

    def special_ability(self):
        return "Backstab"

    def __str__(self):
        return super().__str__() + " (Rogue)"

print("=== Method Overriding Test ===")
print()

warrior = Warrior("Ragnar", 150, 20, 30)
mage = Mage("Gandalf", 70, 25, 200)
rogue = Rogue("Shadow", 100, 18)

print("--- Characters ---")
print(warrior)
print(mage)
print(rogue)
print()

print("--- Attack Values (Overridden) ---")
print(f"{warrior.name} attack: {warrior.attack()}")
print(f"{mage.name} attack: {mage.attack()}")
print(f"{rogue.name} attack: {rogue.attack()}")
print()

print("--- Defense Values (Overridden) ---")
print(f"{warrior.name} defense: {warrior.defend()}")
print(f"{mage.name} defense: {mage.defend()}")
print(f"{rogue.name} defense: {rogue.defend()}")
print()

print("--- Special Abilities (Overridden) ---")
print(f"{warrior.name}: {warrior.special_ability()}")
print(f"{mage.name}: {mage.special_ability()}")
print(f"{rogue.name}: {rogue.special_ability()}")
print()

print("Method overriding complete!")

