import json

WORLD_JSON = '''
{
  "world_name": "Aetheria",
  "regions": [
    {
      "name": "Dark Forest",
      "level_range": [1, 10],
      "cities": [
        {
          "name": "Eldergrove",
          "population": 500,
          "shops": [{"type": "blacksmith", "items": ["sword", "shield"]}]
        }
      ]
    },
    {
      "name": "Mountain Pass",
      "level_range": [10, 20],
      "cities": [
        {
          "name": "Peak Haven",
          "population": 300,
          "shops": [{"type": "alchemist", "items": ["potion", "elixir"]}]
        }
      ]
    }
  ]
}
'''

print("=== Game World Explorer ===")
print()

world = json.loads(WORLD_JSON)
print(f"World: {world['world_name']}")
print()

for region in world['regions']:
    print(f"Region: {region['name']} (Levels {region['level_range'][0]}-{region['level_range'][1]})")
    for city in region['cities']:
        print(f"  City: {city['name']} (Pop: {city['population']})")
        for shop in city['shops']:
            print(f"    Shop: {shop['type']} - {', '.join(shop['items'])}")
    print()

print("World explored!")
