# Exception Handling Demo

def safe_divide(a, b):
    try:
        return a / b
    except ZeroDivisionError:
        print('Error: Cannot divide by zero!')
        return None

print(safe_divide(10, 2))  # 5.0
print(safe_divide(10, 0))  # Error message, returns None

# TODO: Handle ValueError
def safe_int_convert(value):
    try:
        return int(value)
    except ValueError:
        print(f'Error: "{value}" is not a valid number!')
        return 0

print(safe_int_convert('123'))  # 123
print(safe_int_convert('abc'))  # Error, returns 0

solutionCode:
def safe_divide(a, b):
    try:
        return a / b
    except ZeroDivisionError:
        print('Error: Cannot divide by zero!')
        return None

print(safe_divide(10, 2))
print(safe_divide(10, 0))

def safe_int_convert(value):
    try:
        return int(value)
    except ValueError:
        print(f'Error: "{value}" is not a valid number!')
        return 0

print(safe_int_convert('123'))
print(safe_int_convert('abc'))
