import json
import csv
import io
from abc import ABC, abstractmethod

class Character(ABC):
    def __init__(self, name, health, attack):
        self.name = name
        self.health = health
        self.max_health = health
        self.attack = attack
        self.level = 1
        self.experience = 0

    @abstractmethod
    def special_ability(self):
        pass

    def take_damage(self, amount):
        self.health -= amount
        if self.health < 0:
            self.health = 0

    def is_alive(self):
        return self.health > 0

    def gain_xp(self, amount):
        self.experience += amount
        if self.experience >= 100:
            self.level_up()

    def level_up(self):
        self.level += 1
        self.experience = 0
        self.max_health += 10
        self.health = self.max_health
        self.attack += 5
        print(f"{self.name} leveled up to {self.level}!")

    def __str__(self):
        return f"{self.name} Lv{self.level} ({self.health}/{self.max_health} HP)"

class Warrior(Character):
    def __init__(self, name):
        super().__init__(name, 150, 25)

    def special_ability(self):
        return self.attack * 2

class Mage(Character):
    def __init__(self, name):
        super().__init__(name, 70, 35)

    def special_ability(self):
        return self.attack * 1.5

class GameManager:
    def __init__(self):
        self.characters = []

    def add_character(self, character):
        self.characters.append(character)

    def save_game(self):
        data = []
        for char in self.characters:
            data.append({
                'name': char.name,
                'type': char.__class__.__name__,
                'level': char.level,
                'health': char.health,
                'experience': char.experience
            })
        return json.dumps(data, indent=2)

    def export_stats(self):
        output = io.StringIO()
        writer = csv.DictWriter(output, fieldnames=['name', 'level', 'health'])
        writer.writeheader()
        for char in self.characters:
            writer.writerow({'name': char.name, 'level': char.level, 'health': char.health})
        return output.getvalue()

print("=== Complete RPG System ===")
print()

game = GameManager()
game.add_character(Warrior("Ragnar"))
game.add_character(Mage("Gandalf"))

print("Characters:")
for char in game.characters:
    print(f"  {char}")

print("\nSaving game...")
print(game.save_game())

print("\nExporting stats...")
print(game.export_stats())

print("\nüéâ Congratulations! You've completed Python Applications!")
